import React, { Component } from 'react'

import Login from "./Connection/Login";
import Register from "./Connection/Register";

class Connection extends Component {
  constructor(props) {
    super(props)

    this.state = {
      isShowing: true
    }
  }

  toggle = () => {
    // Mise à jour de la vue avec inverseur
    this.setState((state) => {
      return {isShowing: state.isShowing ? false : true}
    });
		this.state.isShowing ? this.setState({ isShowing: false }) : this.setState({ isShowing: true })
	}

  render() {
    return (
      this.state.isShowing
        ?
        <Login hide={this.toggle} />
        :
        <Register hide={this.toggle} />
    )
  }
}

export default Connection
