module.exports = {
  //[ msg = (Le message de base)]  [ toFind = (id | username | mention)]
  validityPseudo: function (value) {
    const x = value.textLength
    switch (true) {
      case (x === 0):
        return {warning: "", valid: false}
      case (x < 3):
        return {warning: "Le pseudo est trop court", valid: false}
      case (x > 32):
        return {warning: "Le pseudo est trop long", valid: false}
        return ""
      default:
        return false
    }
  }
};
