import api from "../../api"

export const initialState = { 
  count: 0 };

function reducer(state) {
  return new Promise(resolve => {

      api.getUserById("f650a09d-c971-4140-ac1b-9a2355d11ce4").then(user => {
        console.log(user)
        resolve({ ...state, count: user.data.data.pseudo });
      })

  });
}

export default reducer;

