import React from 'react'
import api from '../api'

export default React.createContext({
  user: {
    id: "",
    pseudo: ""
  }
});

import io from 'socket.io-client'

const SocketContext = React.createContext()

export function useSocket() {
  return useContext(SocketContext)
}

export async function UserProvider({ id, children }) {

  // Requête a l'api pour rechercher le pseudo
  await api.getUserByPseudo(pseudo.value).then(res => {

    // Je vérifie si le pseudo a été trouver puis si le mot de passe est bon
    // Si tout est ok je récupère l'id et je l'envoi
    if (res.data.data === null) {
      this.setState({
        pseudo: {
          value: pseudo.value,
          warning: 'pseudo inconnu'
        }
      })
    } else if (res.data.data.password !== password.value) {
      this.setState({
        password: {
          value: password.value,
          warning: 'mot de passe invalide'
        }
      })
    } else {
      console.log(`Bienvenue ${res.data.data.pseudo}`)
      // Envoi de l'id
      this.props.onIdSubmit(res.data.data.id)
    }
  })

  return (
    <UserContext.Provider value={socket}>
      {children}
    </UserContext.Provider>
  )
}
