import React, { useRef } from 'react'
import { useUser } from '../../contexts/UserProvider';
import { useChannels } from '../../contexts/ChannelsProvider'

function NewContact({ isShowing, hide }) {
	const idRef = useRef()
	const { user, updateUserConv } = useUser()
	const { createChannel } = useChannels()

	function handleSubmit(e) {
		e.preventDefault()
		// Création du canal de contacts
		createChannel([idRef.current.value])
		hide()
	}

	return (
		isShowing
			?
			<form className="createChannelPrivate" onSubmit={handleSubmit}>
				<div className="createChannelPrivate-header">
					<div className="title">Crée un contact</div>
				</div>
				<div className="createChannelPrivate-body">
					<input className="input" type="text" placeholder="Id" ref={idRef} required />
				</div>
				<div className="createChannelPrivate-footer">
					<button className="btn-text-small" type="submit">Ajouter</button>
				</div>
			</form>
			: null
	)
}
export default NewContact;
