import React, { useState } from 'react'
import api from "../../api"
import { useUser } from '../../contexts/UserProvider';

function NewContact({ isShowing, hide }) {
	const [idInput, setIdInput] = useState('')
	const { user, updateUserConv } = useUser()

	function handleSubmit(e) {
		e.preventDefault()

		api.getUserById(idInput).then(
			(userRes) => {
				const userClient = user
				const userRecipient = userRes.data.data

				const idChannelClient = Math.floor(Math.random() * 10000000000000000) + 99999999999999999;

				var userClientConv = userClient.conversations
				userClientConv.push({
					id: idChannelClient,
					recipients: [{
						id: userRecipient.id,
						pseudo: userRecipient.pseudo
					}],
					messages: []
				})

				const idChannelRecipient = Math.floor(Math.random() * 10000000000000000) + 99999999999999999;

				var userRecipientConv = userRecipient.conversations
				userRecipientConv.push({
					id: idChannelRecipient,
					recipients: [{
						id: userClient.id,
						pseudo: userClient.pseudo
					}],
					messages: []
				})

				// Mise à jour tu client
				updateUserConv(userClient.id, { conversations: userClientConv })

				// Mise à jour tu destinataire
				updateUserConv(userRecipient.id, { conversations: userRecipientConv })
			},
			(error) => {
				console.log(error)
			}
		)

		setIdInput('')
		hide()
	}

	return (
		isShowing
			?
			<form className="createChannelPrivate" onSubmit={handleSubmit}>
				<div className="createChannelPrivate-header">
					<div className="title">Crée un contact</div>
				</div>
				<div className="createChannelPrivate-body">
					<input className="input" type="text" placeholder="Id" required
						value={idInput}
						onChange={e => setIdInput(e.target.value)}
					/>
				</div>
				<div className="createChannelPrivate-footer">
					<button className="btn-text-small" type="submit">Ajouter</button>
				</div>
			</form>
			: null
	)
}
export default NewContact;
