import React, { useContext, useState, useEffect } from 'react'
import useExternalStorage from '../../hooks/useLocalStorage';
import api from "../api"

const UserContext = React.createContext()

export function useUser() {
  return useContext(UserContext)
}

export function UserProvider({ id, children }) {
  const [client, setClient] = useState(null)

  useEffect(() => {
    getClient().then(client => setClient(client))
  }, [])

  // Cette fonction renvoie les données du client
  // - Par défaut elle renvoie les données brutes
  // - Si le type est "cross" elle renvoie les données croisées
  function getClient() {
    return new Promise((resolve) => {
      api.getUserById(id).then(
        (user) => {
          // Constante pour stocker les données brutes du cleint
          const clientRaw = user.data.data

          // Si le client n'a pas de constact j'envoie directement les données
          if (clientRaw.channels.length === 0) return resolve(clientRaw)

          // Variable pour stocker les données croisées du cleint
          var clientCross = user.data.data

          // Je filtre pour ne récupérer que les contacts privés
          clientCross.channels.filter(channel => channel.type === "private").forEach((channel, index) => {

            // Je récupère les informations sur le destinataire grace à sont id
            api.getUserById(channel.recipients.id).then((recipient) => {

              // Si le destinataire n'existe plus je le supprime des données du client
              if (recipient.data.data === null) {
                // Je boucle tous les "channels" du client
                for (let i = 0; i < clientCross.channels.length; i++) {
                  // Je teste si l'id du "channel" sélectionné correspond à l'id du "channel" passé dans la boucle
                  if (clientCross.channels[i].recipients.id === channel.recipients.id) {
                    // Je supprime le "channel" dans la liste grace à sa position
                    clientCross.channels.splice(i, 1);
                    // Je mets à jour le client
                    return api.updateUserById(clientCross.id, { data: { channels: clientCross.channels } }).then(res => {/*//EN COURS// Système gestion des erreurs*/ })
                  }
                }
              }

              // Je complète les informations existantes du destinataire avec celle de la base de données
              channel.recipients = recipient.data.data

              // J'ajoute un element "selected" à "channel" est passe la valeur "true" au premier
              Object.assign(channel, { selected: false })
              Object.assign(channel, { key: index })

              // Je mets à jour l'object "channels" du client 
              clientCross.channels[index] = channel

              if (clientRaw.channels.length === index + 1) return resolve(clientCross)

            });
          })
        })
    })
  }

  function renderClient(client) {
    // Variable pour stocker les données croisées du cleint
    var clientEdit = client

    // Je filtre pour ne récupérer que les contacts privés
    client.channels.filter(channel => channel.type === "private").forEach((channel, index) => {

      // Je récupère les informations sur le destinataire grace à sont id
      api.getUserById(channel.recipients.id).then((recipient) => {

        // Si le destinataire n'existe plus je le supprime des données du client
        if (recipient.data.data === null) return

        // Je complète les informations existantes du destinataire avec celle de la base de données
        channel.recipients = recipient.data.data

        // J'ajoute un element "selected" à "channel" est passe la valeur "true" au premier
        Object.assign(channel, { selected: false })
        Object.assign(channel, { key: index })

        // Je mets à jour l'object "channels" du client 
        clientEdit.channels[index] = channel

        if (client.channels.length === index + 1) {
          setClient(clientEdit)
        }
      });
    })
  }

  function updateUser(id, body) {

    api.getUserById(id).then(
      (user) => {

      }
    )

    const payload = body
    api.updateUserById(id, payload)
  }

  const value = {
    client,
    setClient,
    renderClient,
    updateUser
  }

  return (
    <UserContext.Provider value={value}>
      {children}
    </UserContext.Provider>
  )
}

export default UserContext

// https://dmitripavlutin.com/react-usestate-hook-guide/
