import React, { Component } from 'react'
import Sidebar from './Sidebar';
import OpenConversation from './OpenConversation';

import UserContextTest from '../contexts/ConversationsProvider';

import api from "../api"

// Main
class Dashboard extends Component {
  static contextType = UserContextTest
  constructor(props) {
    super(props);
    this.state = {
      selectedConversation: null,
      error: null,
      isLoaded: false,
      user: []
    };
  }

  componentDidMount() {
    const user = this.context

    console.log(user) // { name: 'Tania', loggedIn: true }

    api.getUserById("f650a09d-c971-4140-ac1b-9a2355d11ce4").then(
      (user) => {
        this.setState({
          isLoaded: true,
          user: user.data.data
        });
      },
      (error) => {
        this.setState({
          isLoaded: true,
          error
        });
      }
    )
  }

  render() {
    const { selectedConversation, error, isLoaded, user } = this.state;

    return (
      <>
        <Sidebar user={user} />
        <div className="container">
          {
            selectedConversation !== undefined ?
              selectedConversation && <OpenConversation />
              :
              <div className="welcome">
                <div className="welcome-title">
                  Bienvenue sur FInt.Tchat
                </div>
                <div className="welcome-content">
                  Pour ajouter un contact il faut cliquer sur le petit + a coté de contacts,
                  puis entrer son id et cliquez sur ajouter
                </div>
              </div>
          }
        </div>
      </>
    )
  }
}

export default Dashboard

