import { useEffect, useState } from 'react';
import useLocalStorage from '../hooks/useLocalStorage';
import api from "../api"

export default function useExternalStorage() {
  // Récupération de l'id du client pour pouvoir identifier la cible de la requête
  const [id] = useLocalStorage('id')
  // Initialisation du "useState" pour stocké les données
  const [value, setValue] = useState(null);

  // Le "useEffect" est utilisé pour géré l'asynchrone
  useEffect(() => {
    // Fonction asynchrone pour faire le rendu des channels du client
    async function fetchClient() {
      api.getUserById(id).then(
        (user) => {
          return setValue(user.data.data)
      })
    }
    var use = false
    // Appel de la fonction
    if (use === false) {
      use = true
      fetchClient();
    }
  }, [id])
  //console.log('appel externe')
  return [value, setValue]
}
