import React, { Component } from 'react'
import Sidebar from './Sidebar';
import OpenConversation from './OpenConversation';
import { useConversations } from '../contexts/ConversationsProvider';
// Main
class Dashboard extends Component {
  // id = this.props.id
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    api.getUserById("f650a09d-c971-4140-ac1b-9a2355d11ce4").then(user => {
      this.setState({
        isLoaded: true,
        items: user.data.data 
      });
    })
    fetch("https://api.example.com/items")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
        // Remarque : il est important de traiter les erreurs ici
        // au lieu d'utiliser un bloc catch(), pour ne pas passer à la trappe
        // des exceptions provenant de réels bugs du composant.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }

  /*const req = api.getUserById("f650a09d-c971-4140-ac1b-9a2355d11ce4").then(user => { return user.data.data })
  const res = async () => {
    return await req
  };
  console.log(res())
  setState(res());*/

  render() {
    const { selectedConversation } = useConversations();
    return (
      <>
        <Sidebar />
        <div className="container">
          {
            selectedConversation !== undefined ?
              selectedConversation && <OpenConversation />
              :
              <div className="welcome">
                <div className="welcome-title">
                  Bienvenue sur FInt.Tchat
                </div>
                <div className="welcome-content">
                  Pour ajouter un contact il faut cliquer sur le petit + a coté de contacts,
                  puis entrer son id et cliquez sur ajouter
                </div>
              </div>
          }
        </div>
      </>
    )
  }
}

export default Dashboard
