import React, { useState } from 'react'
import api from "../../api"
import { useUser } from '../../contexts/UserProvider';

function NewContact({ isShowing, hide }) {
	const { user, updateUser } = useUser()
	const [id, setId] = useState({
		value: "",
		warning: "",
		valid: false
	})

	function handleSubmit(e) {
		e.preventDefault()

		// Je test si l'id n'est pas celui du client
		if (id.value === user.id) return setId({
			value: "",
			warning: "Tu ne peux pas t'ajouter",
			valid: false
		})

		api.getUserById(id.value).then(
			(userRecipient) => {

				// Je test si l'id est dans la base de données
				if (userRecipient.data.data === null) return setId({
					value: "",
					warning: "L'id n'a pas été trouvé",
					valid: false
				})

				const userClient = user
				userRecipient = userRecipient.data.data

				// Id pour les deux
				const idChannel = Math.floor(Math.random() * 10000000000000000) + 99999999999999999;

				// Insertion du "channel" pour le client
				const idChannelClient = Math.floor(Math.random() * 10000000000000000) + 99999999999999999;

				var userClientChannels = userClient.channels
				userClientChannels.push({
					id: idChannel,
					singleId: idChannelClient,
					type: "private",
					recipient: {
						id: userRecipient.id
					},
					messages: []
				})

				// Insertion du "channel" pour le destinataire
				const idChannelRecipient = Math.floor(Math.random() * 10000000000000000) + 99999999999999999;

				var userRecipientChannels = userRecipient.channels
				userRecipientChannels.push({
					id: idChannel,
					singleId: idChannelRecipient,
					type: "private",
					recipient: {
						id: userClient.id
					},
					messages: []
				})

				// Mise à jour du client
				updateUser(userClient.id, { data: { channels: userClientChannels } })
				// Mise à jour du destinataire
				updateUser(userRecipient.id, { data: { channels: userRecipientChannels } })

				setId({
					value: "",
					warning: "",
					valid: false
				})
				hide()
			},
			(error) => {
				console.log(error)
			}
		)
		
	}

	return (
		isShowing
			?
			<form className="createChannelPrivate" onSubmit={handleSubmit}>
				<div className="createChannelPrivate-header">
					<div className="title">Ajouter un contact</div>
				</div>
				<div className="createChannelPrivate-body">
					{id.warning ? <div className="title-warning">{id.warning} </div> : null}
					<input className="input" type="text" placeholder="Id" required
						value={id.value}
						onChange={e => setId({
							value: e.target.value,
							warning: "",
							valid: false
						})}
					/>
				</div>
				<div className="createChannelPrivate-footer">
					<button className="btn-text-small" type="submit">Ajouter</button>
				</div>
			</form>
			: null
	)
}
export default NewContact;

