import React, { useContext, useState, useEffect } from 'react'
import useExternalStorage from '../hooks/useExternalStorage';
import api from "../api"

const UserContext = React.createContext()

export function useUser() {
  return useContext(UserContext)
}

export function UserProvider({ id, children }) {
  const [client, setClient] = useState(null)

  useEffect(() => {
    if (client === null) {
      // Je récupère les données du client grace à l'api
      api.getUserById(id).then(
        (user) => {
          return setClient(user.data.data)
      })
    }
  }, [client])

  function updateUser(id, body) {
    const payload = body
    api.updateUserById(id, payload)
  }

  const value = {
    client,
    setClient,
    updateUser
  }

  return (
    <UserContext.Provider value={value}>
      {children}
    </UserContext.Provider>
  )
}

export default UserContext

