import React, { useState } from 'react'

import api from "../../api"

import { useUser } from '../../contexts/UserProvider';

function NewContact({ isShowing, hide }) {
	const [idInput, setIdInput] = useState('')
	const { user, updateUserConv } = useUser()

	function handleSubmit(e) {
		e.preventDefault()
		console.log(user)
		console.log(idInput)

		api.getUserById(idInput).then(
			(userRes) => {
				console.log(userRes.data.data)
				var userClientConv = user.conversations
				userClientConv.push({
					recipients: [{
						id: userRes.id,
						name: userRes.name
					}],
					messages: []
				})

				updateUserConv(userRes.id, { conversations: userClientConv })
			},
			(error) => {
				console.log(error)
			}
		)
		setIdInput('')
		hide()
	}

	return (
		isShowing
			?
			<form className="createChannelPrivate" onSubmit={handleSubmit}>
				<div className="createChannelPrivate-header">
					<div className="title">Cr√©e un contact</div>
				</div>
				<div className="createChannelPrivate-body">
					<input className="input" type="text" placeholder="Id"
						ref={idRef} required
						value={idInput}
						onChange={e => setIdInput(e.target.value)}
					/>
				</div>
				<div className="createChannelPrivate-footer">
					<button className="btn-text-small" type="submit">Ajouter</button>
				</div>
			</form>
			: null
	)
}
export default NewContact;
