import React, { Component, useContext } from 'react'

import api from "../api"

import SocketContext from './SocketProvider';

const UserContext = React.createContext()

export function useUser() {
  return useContext(UserContext)
}

export class UserProvider extends Component {
  static contextType = SocketContext

  updateUser = async (id, body) => {
    const payload = body
    await api.updateUserById(id, payload).then(res => {
    })
  }

  getUser = () => {
    return new Promise((resolve) => {
      api.getUserById(this.props.id).then(
        (user) => {
          const oldUser = user.data.data
          var newUser = user.data.data
          
          // Si le client n'a pas de constact j'envoie directement les données
          if (newUser.channels.length === 0) resolve(newUser)

          // Je filtre pour récupérer que les contacts privés
          newUser.channels.filter(channel => channel.type === "private").forEach((channel, index) => {

            // j’attent que la fonction soit terminée
            api.getUserById(channel.recipients.id).then((recipient) => {
              

              if (recipient.data.data === null) {
                // Supression du channel dans les données du client
                var userClient = user
                for (let i = 0; i < userClient.channels.length; i++) {
                  if (userClient.channels[i].id === selectedChannel.id) {
                    var userClientChannels = userClient.channels;
                    userClientChannels.splice(i, 1)
                    updateUser(userClient.id, { data: { channels: userClientChannels } })
                  }
                }
              }

              // Je complète les informations existantes du destinataire avec celle de la base de données
              channel.recipients = recipient.data.data

              // J'ajoute un element "selected" à "channel" est passe la valeur "true" au premier
              Object.assign(channel, { selected: false })
              Object.assign(channel, { key: index })

              // Je mets à jour l'object "channels" du client 
              newUser.channels[index] = channel

              if (oldUser.channels.length === index + 1) resolve(newUser)

            });
          })
        })
    })
  }

  createUser = () => {

  }

  deleteUser = () => {

  }

  render() {
    const value = {
      updateUser: this.updateUser,
      getUser: this.getUser
    }

    return (
      <UserContext.Provider value={value}>
        {this.props.children}
      </UserContext.Provider>
    )
  }
}

export default UserContext
