import React, { useContext, useState, useEffect } from 'react'
import api from "../api"

const UserContext = React.createContext()

export function useUser() {
  return useContext(UserContext)
}

export function UserProvider({ id, children }) {
  const [client, setClient] = useState(null)

  useEffect(() => {
    // Si le client n'est pas null pas besoin de mise a jour
    if (client === null) {
      // Je récupère les données du client grace à l'api
      api.getUserById(id).then(
        (response) => {
          var userEdit = response.data.user
          // Assignation de la valeur "avatar" à "userEdit"
          Object.assign(userEdit, { avatar: `http://localhost:3000/api/file_avatar/id/101584218257301550` })// https://collaps.xyz/collapsapi/api/file_avatar/id/ //${userEdit.id}
          console.log(userEdit)
          return setClient(userEdit)//
      })
    }
  }, [id, client])

  function updateUser(id, body) {
    const payload = body
    api.updateUserById(id, payload)
  }

  const value = {
    client,
    setClient,
    updateUser
  }

  return (
    <UserContext.Provider value={value}>
      {children}
    </UserContext.Provider>
  )
}

export default UserContext

