import React, { useState, useEffect } from 'react';

import api from '../../api';
import { useUser } from '../../contexts/UserProvider';
import { useChannels } from '../../contexts/ChannelsProvider';

export default function ChannelsContacts() {
  const [channels, setChannels] = useState([]);
  const { setSelectedChannel } = useChannels()
  const { user } = useUser()

  useEffect(() => {
    // Je teste si les données utilisateur sont chargées
    if (user !== null) {
      // Je teste si tous les contact sont chargés pour evité la boucle infini
      if (channels.length !== user.channels.length) {

        // Je filtre pour récupérer que les contacts privés
        user.channels.filter(channel => channel.type === "private").map((channel) => {
          console.log(channel)
          // Je récupère le destinataire dans la base de données
          api.getUserById(channel.recipients.id).then(
            (channelRecipient) => {
              channelRecipient = channelRecipient.data.data 
              channel.recipients = channelRecipient
              console.log(channel)
              setChannels([...channels, { channel, channelRecipient }])
            },
            (error) => {
              console.log(error)
            }
          )
        })

      }
    }
  });

  return (
    <>
      { channels.map((channel) => (
        <button
          key={channel.channel.singleId}
          className={`sidebar-body-contact ${channel.channel.selected ? 'selected' : ''}`}
          onClick={() => setSelectedChannel(channel.channel)}
        >
          <div className="sidebar-body-contact-content">
            <div className="sidebar-body-contact-content-avatar">

              <svg width="40" height="32" viewBox="0 0 40 32" aria-hidden="true">
                <foreignObject x="0" y="0" width="32" height="32">
                  <img className="avatar" src="https://wir.skyrock.net/wir/v1/profilcrop/?c=mog&w=301&h=301&im=%2Fart%2FPRIP.105317501.2.2.jpg" aria-hidden="true" alt="" />
                </foreignObject>
                <rect className="online" fill="currentColor" width="10" height="10" x="22" y="22" rx="15" ry="15"></rect>
              </svg>

            </div>
            <div className="sidebar-body-contact-content-text">
              <div className="username">
                <div className="overflow">
                  {channel.channelRecipient.pseudo}
                </div>
              </div>
            </div>
          </div>
        </button>
      ))
      }
    </>
  )
}
