      </div>
      <div className="message-body scroller">
        {
          selectedChannel.messages.map((message, index) => {

            var date = new Date(message.date);
            date = `${date.getDate()}/${date.getMonth() + 1}/${date.getFullYear()} ${date.getHours()}:${date.getMinutes()}`

            const pseudo = client === null ? null : message.sender === client.id ? client.pseudo : selectedChannel.recipients.find(recipient => recipient.id === message.sender).pseudo

            // Détecte si plus de deux messages a la suite sont du même destinataire
            var lastSenderPass = lastSenderNow;
            lastSenderNow = message.sender;

            return (
              lastSenderNow !== lastSenderPass
                ?
                <div
                  className="message-body-message parents"
                  key={index}
                >
                  <div className="message-body-message-avatar">
                    <img className="avatar"
                      src={`http://localhost:3000/api/file_avatar/id/${message.sender}`}
                      alt="user profil" />
                  </div>
                  <h2 className="message-body-message-header">
                    <span className="pseudo">
                      {pseudo}
                    </span>
                    <span className="timestamp">
                      {date}
                    </span>
                  </h2>
                  <div className="message-body-message-text">
                    {message.text}
                  </div>
                </div>
                :
                <div
                  className="message-body-message children"
                  key={index}
                >
                  <div className="message-body-message-text">
                    {message.text}
                  </div>
                </div>
            )
          })
        }
        {
          messagesChannels.length === 0 ? null : messagesChannels.find(messagesChannel => messagesChannel.id === selectedChannel.id).messages.map((message, index) => {

            const lastMessage = selectedChannel.messages.length - 1 === index

            var date = new Date(message.date);
            date = `${date.getDate()}/${date.getMonth() + 1}/${date.getFullYear()} ${date.getHours()}:${date.getMinutes()}`

            // Détecte si plus de deux messages a la suite sont du même destinataire
            var lastSenderPass = lastSenderNow;
            lastSenderNow = message.sender.id;
            console.log(message.sender)
            return (
              lastSenderNow !== lastSenderPass
                ?
                <div
                  className="message-body-message parents"
                  key={index}
                  ref={lastMessage ? setRef : null}
                >
                  <div className="message-body-message-avatar">
                    <img className="avatar"
                      src={`http://localhost:3000/api/file_avatar/id/${message.sender.id}`}
                      alt="user profil" />
                  </div>
                  <h2 className="message-body-message-header">
                    <span className="pseudo">
                      {message.sender.pseudo}
                    </span>
                    <span className="timestamp">
                      {date}
                    </span>
                  </h2>
                  <div className="message-body-message-text">
                    {message.text}
                  </div>
                </div>
                :
                <div
                  className="message-body-message children"
                  key={index}
                >
                  <div className="message-body-message-text">
                    {message.text}
                  </div>
                </div>
            )
          })
        }
        <div className="message-body-spacer"></div>
      </div>
