			const [isShowingNewContact, setIsShowing] = useState(false);
			// Inverseur
			function toggleNewContact() { setIsShowing(!isShowingNewContact) };
			// Return
			return { isShowingNewContact, toggleNewContact };
		};
		const { isShowingNewContact, toggleNewContact } = useModalNewContact();
		this.setState({isShowingNewContact: isShowingNewContact, toggleNewContact: toggleNewContact})
