			const [isShowingNewGroupe, setIsShowing] = useState(false);
			// Inverseur
			function toggleNewGroupe() { setIsShowing(!isShowingNewGroupe) }
			// Return
			return { isShowingNewGroupe, toggleNewGroupe };
		};
		const { isShowingNewGroupe, toggleNewGroupe } = useModalNewGroupe();

		// Affiche la fenêtre de paramètre
		const useModalSetting = () => {
			const [isShowingSetting, setIsShowing] = useState(false);
			// Inverseur
			function toggleSetting() { setIsShowing(!isShowingSetting) }
			// Return
			return { isShowingSetting, toggleSetting };
		};
		const { isShowingSetting, toggleSetting } = useModalSetting();

		// Affiche la fenêtre de sécurité
		const useModalSecurity = () => {
			const [isShowingSecurity, setIsSecurity] = useState(false);
			// Inverseur
			function toggleSecurity() { setIsSecurity(!isShowingSecurity) }
			// Return
			return { isShowingSecurity, toggleSecurity };
		};
		const { isShowingSecurity, toggleSecurity } = useModalSecurity();*/
	}

	render() {
		const { users, isLoading } = this.state
		//const id = this.props.id;
		const id = "fghgh";
		console.log(id);
		console.log(users);
		console.log(this.state);
		// [ Return ]
		return (
			<div className="sidebar">
				<div className="sidebar-header">
					<input className="sidebar-header-search" type="text" placeholder="Rechercher un(e) conversation/groupe" />
				</div>
				<div className="sidebar-body scroller">

					<div className="title">
						<span className="text">Conversations</span>
						<button className="button" type="button" aria-label="Créer un MP" onClick={this.state.toggleNewContact}>
							<svg x="0" y="0" aria-hidden="false"
								width="24" height="24" viewBox="0 0 18 18">
								<polygon fillRule="nonzero" fill="currentColor" points="15 10 10 10 10 15 8 15 8 10 3 10 3 8 8 8 8 3 10 3 10 8 15 8"></polygon>
							</svg>
						</button>
					</div>
					<div className="private-channels" id="channelsPrivates" aria-label="Salons privés">
						<NewContact isShowing={this.state.isShowingNewContact} hide={this.state.toggleNewContact} />
						<Conversations />
					</div>
					<div className="title" id="channelsGroupesTitle">
						<span className="text">Contacts</span>
						<button className="button" type="button" aria-label="Créer un Groupe">
							<svg x="0" y="0" aria-hidden="false"
								width="24" height="24" viewBox="0 0 18 18">
								<polygon fillRule="nonzero" fill="currentColor" points="15 10 10 10 10 15 8 15 8 10 3 10 3 8 8 8 8 3 10 3 10 8 15 8"></polygon>
							</svg>
						</button>
					</div>
					<div className="groupe-channels" id="channelsGroupes" aria-label="Salons de groupes">

					</div>

