import React, { useState, useEffect } from 'react';

import api from '../../api';
import { useUser } from '../../contexts/UserProvider';
import { useChannels } from '../../contexts/ChannelsProvider';

export default function ChannelsContacts() {
  // Création d'un "useState" pour stocker les "channels" dans une liste
  const [channels, setChannels] = useState([]);
  // Récupération des informations du client
  const { user } = useUser()
  // Récupération de la fonction pour sélectionner un "channel"
  const { setSelectedChannel } = useChannels()

  // Je charge les données
  useEffect(() => {
    // Je teste si les données utilisateur sont chargées
    if (user !== null) {

      // Je teste si tous les contact sont chargés pour evité la boucle infini
      if (channels.length !== user.channels.length) {

        // Je filtre pour récupérer que les contacts privés
        user.channels.filter(channel => channel.type === "private").forEach(channel => {
          var i = -1; i++;

          async function getRecipient(id) {
            // Je récupère le destinataire dans la base de données
            api.getUserById(channel.recipients.id).then(
              (channelRecipient) => {
                // Je complète les informations existantes du destinataire avec celle de la base de données
                channel.recipients = channelRecipient.data.data
              },
              (error) => {
                console.log(error)
              }
            )
          }

          console.log(channel)
          // J'ajoute un element "selected" à "channel" est passé la valeur "true" au premier
          Object.assign(channel, { selected: i === 0 ? true : false })

          // J'ajoute le channel a la liste des "channels"
          setChannels([...channels, channel])

          // Je sélection le premier "channel"
          if (i === 0) setSelectedChannel(channel)
        })
      }
    }
  });

  return (
    <>
      { channels.map((channel) => (
        <button
          key={channel.singleId}
          className={`sidebar-body-contact ${channel.selected ? 'selected' : ''}`}
          onClick={() => setSelectedChannel(channel)}
        >
          <div className="sidebar-body-contact-content">
            <div className="sidebar-body-contact-content-avatar">

              <svg width="40" height="32" viewBox="0 0 40 32" aria-hidden="true">
                <foreignObject x="0" y="0" width="32" height="32">
                  <img className="avatar" src="https://wir.skyrock.net/wir/v1/profilcrop/?c=mog&w=301&h=301&im=%2Fart%2FPRIP.105317501.2.2.jpg" aria-hidden="true" alt="" />
                </foreignObject>
                <rect className="online" fill="currentColor" width="10" height="10" x="22" y="22" rx="15" ry="15"></rect>
              </svg>

            </div>
            <div className="sidebar-body-contact-content-text">
              <div className="username">
                <div className="overflow">
                  {channel.recipients.pseudo}
                </div>
              </div>
            </div>
          </div>
        </button>
      ))
      }
    </>
  )
}
