import React, { useContext, useState, useEffect, useRef } from 'react'
import { useSocket } from './SocketProvider';
import api from "../api"

const ChannelsContext = React.createContext()

export function useChannels() {
  return useContext(ChannelsContext)
}

export function ChannelsProvider({ id, children }) {
  const socket = useSocket()
  // https://stackoverflow.com/questions/54824036/useeffect-hook-with-socket-io-state-is-not-persistent-in-socket-handlers
  const [channels, setChannels] = useState(null)
  const channelsRef = useRef(channels);
  useEffect(() => {
    channelsRef.current = channels;
  });

  const [messagesChannels, setMessagesChannels] = useState([])
  const messagesChannelsRef = useRef(messagesChannels);
  useEffect(() => {
    messagesChannelsRef.current = messagesChannels;
  });

  const [selectedChannel, setSelectedChannel] = useState(null)

  useEffect(() => {
    if (socket == null) return

    const addMessage = (message) => {
      // J'isole les "channels" dans une variable
      var allMessages = messagesChannelsRef.current

      // Récuépration de l'index
      const indexMessage = allMessages.findIndex(messagesChannel => messagesChannel.id === message.channelId);

      if (indexMessage !== -1) {
        // Copie de l'ancien tableau de données
        let newArr = [...messagesChannelsRef.current];
        // Ajout du nouveau message
        newArr[indexMessage].messages = [...newArr[indexMessage].messages, {sender: message.sender, date: message.date, text: message.text}];
        // Envoie des données dans le "useState"
        setMessagesChannels(newArr);
      } else {
        // Ajout du message dans le "useState"
        setMessagesChannels(msg => [...msg, { id: message.channelId, messages: [{sender: message.sender, date: message.date, text: message.text}] }])
      }
    }

    // Si je reçoi un message je l'ajoute dans le "channel" qui lui correspond
    socket.on('receive-message', addMessage);

    return () => socket.off('receive-message', addMessage)
  }, [socket])

  function addMessageToDatabase(channel, text, client) {
    //channel.id

    // Récupération des données du "channel" avec son id
    api.getChannelById(channel.id).then(
      (channel) => {

        console.log(channel)

        channel.messages.push({
          index: channel.messages.length + 1,
          sender: client.id,
          date: new Date(),
          text: text
        })

        api.updateUserById(channel.id, { data: { messages: channel } }).then(res => { })

      },
      (error) => {
        console.log(error)
      }
    )
  }

  function sendMessage(channel, text, client, recipients) {
    socket.emit('send-message', { channelId: channel.id, recipients, date: new Date(), text })

    addMessageToDatabase(channel, text, client)
  }

  // Cette fonction renvoie les données du client
  function formattedChannel() {
    return new Promise((resolve) => {

      // Récupération des données du "channel" avec son id
      api.getChannelById(selectedChannel.id).then(
        (channel) => {

          console.log(channel)
          console.log(selectedChannel)
          resolve(selectedChannel)

        },
        (error) => {
          console.log(error)
        }
      )

    })
  }

  /*
  conversations.map((conversation, index) => {
    const recipients = conversation.recipients.map(recipient => {
      const contact = contacts.find(contact => {
        return contact.id === recipient
      })
      const name = (contact && contact.name) || recipient
      return { id: recipient, name }
    })

    const messages = conversation.messages.map(message => {
      const contact = contacts.find(contact => {
        return contact.id === message.sender
      })
      const name = (contact && contact.name) || message.sender
      const fromMe = id === message.sender
      return { ...message, senderName: name, fromMe }
    })

    const selected = index === selectedConversationIndex
    return { ...conversation, messages, recipients, selected }
  })*/

  function updateUser(id, body) {

    api.getUserById(id).then(
      (user) => {
      }
    )
    const payload = body
    api.updateUserById(id, payload)
  }

  const value = {
    channels,
    setChannels,
    messagesChannels,
    setMessagesChannels,
    selectedChannel,
    setSelectedChannel,
    createChannel,
    formattedChannel,
    sendMessage
  }

  return (
    <ChannelsContext.Provider value={value}>
      {children}
    </ChannelsContext.Provider>
  )
}

export default ChannelsContext

