import React from 'react'
import { useConversations } from '../contexts/ConversationsProvider';

export default function Conversations() {
  const { conversations, selectConversationIndex } = useConversations()

  return (
    <>
      {conversations.map((conversation, index) => (
        <button
          key={index}
          className={`contact-body-contact ${conversation.selected ? 'selected' : ''}`}
          onClick={() => selectConversationIndex(index)}
        >
          <div className="contact-body-contact-content">
            <div className="contact-body-contact-content-avatar">

              <svg width="40" height="32" viewBox="0 0 40 32" aria-hidden="true">
                <foreignObject x="0" y="0" width="32" height="32">
                  <img className="avatar" src="https://wir.skyrock.net/wir/v1/profilcrop/?c=mog&w=301&h=301&im=%2Fart%2FPRIP.105317501.2.2.jpg" aria-hidden="true" alt="" />
                </foreignObject>
                <rect className="online" fill="currentColor" width="10" height="10" x="22" y="22" rx="15" ry="15"></rect>
              </svg>

            </div>
            <div className="contact-body-contact-content-text">
              <div className="username">
                <div className="overflow">{conversation.recipients.map(r => r.name).join(', ')}</div>
              </div>
            </div>
          </div>
        </button>
      ))}
    </>
  )
}
