import React, { useState, useEffect } from 'react'
import api from "../../api"
import { useUser } from '../../contexts/UserProvider';

function NewContact({ isShowing, hide }) {
	const { getUser, updateUser } = useUser()

	// Création d'un "useState" pour stocker les données du client
	const [user, setUser] = useState();

	// Création d'un "useState" pour stocker l'id
	const [id, setId] = useState({
		value: "",
		warning: ""
	})

	// Je charge les données
	useEffect(() => {
		getClient().then(client => setUser(client))
		// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [])

	function handleSubmit(e) {
		e.preventDefault()

		// Je teste si la valeur est bien celle d'un id
		if (id.value.length === 0) return setId({
			value: "",
			warning: "Il faut entré un id"
		})

		// Je teste si la valeur est bien celle d'un id
		if (id.value.length !== 18 && !/^[0-9]*$/.test(id.value)) return setId({
			value: "",
			warning: "Ceci n'est pas un id valide"
		})

		// Je test si l'id n'est pas celui du client
		if (id.value === user.id) return setId({
			value: "",
			warning: "Tu ne peux pas t'ajouter"
		})

		// Je test si le contact n'est pas déjà ajouter
		var valid = true;
		user.channels.filter(channel => channel.type === "private").forEach(channel => {
			if (channel.recipients.id === id.value) valid = false;
		});
		if (valid === false) return setId({
			value: "",
			warning: "Ce contact existe déjà"
		});

		api.getUserById(id.value).then(
			(recipient) => {

				// Je test si l'id n'est pas déjà dans la base de données
				if (recipient.data.data === null) return setId({
					value: "",
					warning: "L'id n'a pas été trouvé"
				})

				const client = user
				recipient = recipient.data.data

				// Id pour les deux
				const idChannel = Math.floor(Math.random() * 10000000000000000) + 99999999999999999;

				var clientChannels = client.channels
				clientChannels.push({
					id: idChannel,
					type: "private",
					recipients: {
						id: recipient.id
					},
					messages: []
				})

				var recipientChannels = recipient.channels
				recipientChannels.push({
					id: idChannel,
					type: "private",
					recipients: {
						id: client.id
					},
					messages: []
				})

				// Mise à jour du client
				updateUser(client.id, { data: { channels: clientChannels } })
				// Mise à jour du destinataire
				updateUser(recipient.id, { data: { channels: recipientChannels } })

				setId({
					value: "",
					warning: ""
				})
				hide()
			},
			(error) => {
				console.log(error)
			}
		)

	}

	return (
		isShowing
			?
			<form className="createChannelPrivate" onSubmit={handleSubmit}>
				<div className="createChannelPrivate-header">
					<div className="title">Ajouter un contact</div>
				</div>
				<div className="createChannelPrivate-body">
					{id.warning ? <div className="title-warning">{id.warning} </div> : null}
					<input className="input" type="text" placeholder="Id"
						value={id.value}
						onChange={e => setId({
							value: e.target.value,
							warning: ""
						})}
					/>
				</div>
				<div className="createChannelPrivate-footer">
					<button className="btn-text-small" type="submit">Ajouter</button>
				</div>
			</form>
			: null
	)
}
export default NewContact;
