import React, { Component } from 'react'
import api from '../../api'

class Register extends Component {
  constructor(props) {
    super(props)

    this.state = {
      pseudo: {
        value: '',
        warning: ''
      },
      password: {
        value: '',
        warning: ''
      }
    }
  }

  handleChangeInputPseudo = async (event) => {
    const pseudo = event.target.value
    this.setState({
      pseudo: {
        value: pseudo,
        warning: ''
      }
    })
  }

  handleChangeInputPassword = async (event) => {
    const password = event.target.value
    this.setState({
      password: {
        value: password,
        warning: ''
      }
    })
  }

  handleLoginUser = async () => {
    const { pseudo, password } = this.state

    if (pseudo.length !== null && password.length !== null) {
      await api.getUserByPseudo(pseudo).then(res => {
        if (res.data.success !== true) {
          this.setState({
            pseudo: {
              value: pseudo,
              warning: 'pseudo inconue'
            }
          })
        } else if (res.data.data.password) {

        }
      })
    }
  }

  render() {
    const { pseudo, password } = this.state
    return (
      <div className="connection">
        <form className="connection-form">
          <div className="connectionToolHeader">
            <div className="connectionToolHeader-title">
              FInt.Tchat
          </div>
            <div className="connectionToolHeader-subtitle">
              Bienvenue dans l'espace de connexion
          </div>
          </div>
          <div className="connection-body scroller">
            <div className="connection-body-input">
              <div className="title">Pseudo</div>
              <input
                className="input"
                type="text"
                autoFocus
                required
                value={pseudo}
                onChange={this.handleChangeInputPseudo}
              />
              <div className="spacer"></div>
              <div className="title">Mot de passe</div>
              <input
                className="input"
                type="password"
                required
                value={password}
                onChange={this.handleChangeInputPassword}
              />
              <div className="link"><a href="https://google.com">Tu as oubli√© ton mot de passe ?</a></div>
            </div>
          </div>
          <div className="connectionToolFooter">
            <div className="connectionToolFooter-btn">
              <button className="btn-text-default" type="submit" onClick={this.handleLoginUser}>
                <div className="content">Se connecter</div>
              </button>
              <button className="btn-text-simple" type="button" onClick={this.props.hide}>
                <div className="content">S'inscrire</div>
              </button>
            </div>
          </div>
        </form>
      </div>
    )
  }
}

export default Register
