module.exports = {
  //[ msg = (Le message de base)]  [ toFind = (id | username | mention)]
  validityPseudo: function (value) {
    const valueLength = value.textLength
    switch (true) {
      case (valueLength === 0):
        return null
      case (valueLength < 3):
        return "Le pseudo est trop court"
      case (valueLength > 32):
        return "Le pseudo est trop long"
      default:
        return false
    }
  }
};
