import React, { Component } from 'react'
import Sidebar from './sidebar/Sidebar';

import Welcome from './container/Welcome';
import OpenConversation from './container/OpenConversation';

import ConversationsContext from '../contexts/ConversationsProvider';

import api from "../api"

// Main
class Dashboard extends Component {
  static contextType = ConversationsContext
  constructor(props) {
    super(props);
    this.state = {
      selectedConversation: null,
      error: null,
      isLoaded: false,
      user: []
    };
  }

  render() {
    const { /*error, isLoaded,*/ user } = this.state;
    const selectedConversation = this.context.selectedConversation

    return (
      <>
        <Sidebar user={user} />
        <div className="container">
          {
            selectedConversation !== undefined ? selectedConversation && <OpenConversation /> : <Welcome />
          }
        </div>
      </>
    )
  }
}

export default Dashboard
