import React, { Component, useRef } from 'react'
import { v4 as uuidV4 } from 'uuid'

class Register extends Component {
	constructor(props) {
		super(props)
		console.log(props);
		this.state = {
			toggle: props.hide
		}
	}

  handleSubmit = () => {
    //const idRef = useRef()
    console.log(this)
    //this.props.onIdSubmit(idRef.current.value)
  }

	render() {
    /*
		function createNewId() {
      //this.onIdSubmit(uuidV4())
    }*/
		return (
			<div className="connection">
				<form className="connection-form">
					<div className="connectionToolHeader">
						<div className="connectionToolHeader-title">
							FInt.Tchat
            </div>
						<div className="connectionToolHeader-subtitle">
							Bienvenue dans l'espace d'inscription
            </div>
					</div>
					<div className="connection-body scroller">
						<div className="connection-body-input">
							<div className="title">Pseudo</div>
							<input className="input" type="text" autoFocus required />
							<div className="spacer"></div>
							<div className="title">Mot de passe</div>
							<input className="input" type="password" required />
							<div className="spacer"></div>
							<div className="title">Confirmer le mot de passe</div>
							<input className="input" type="password" required />
						</div>
					</div>
					<div className="connectionToolFooter">
						<div className="connectionToolFooter-btn">
							<button className="btn-text-default" type="button" onClick={this.handleSubmit}>
								<div className="content">S'inscrire</div>
							</button>
							<button className="btn-text-simple" type="button" onClick={this.state.toggle}>
								<div className="content">Se connecter</div>
							</button>
						</div>
					</div>
				</form>
			</div>
		)
	}
}

export default Register
