module.exports = {
  //[ msg = (Le message de base)]  [ toFind = (id | username | mention)]
  validityPseudo: function (value) {
    const x = value.textLength
    switch (true) {
      case (x === 0):
        return null
      case (x < 3):
        return "Le pseudo est trop court"
      case (x > 32):
        return "Le pseudo est trop long"
      default:
        return false
    }
  }
};
