import axios from 'axios'

const api = axios.create({
    baseURL: 'http://localhost:3000/api', //https://collaps.xyz/collapsapi/api //http://localhost:3000/api
})

// User
export const registeringUser = (payload) => api.post(`/user`, payload)

export const loginUser = (id) => api.get(`/user/login/${id}`)
export const getUserById = (id) => api.get(`/user/id/${id}`)
export const getUserByPseudo = (pseudo) => api.get(`/user/pseudo/${pseudo}`)

export const updateUserById = (id, payload) => api.put(`/user/${id}`, payload)

export const deleteUserById = (id) => api.delete(`/user/${id}`)

//Channel
export const insertChannel = (payload) => api.post(`/channel`, payload)

export const getChannelById = (id) => api.get(`/channel/id/${id}`)

export const updateChannelById = (id, payload) => api.put(`/channel/${id}`, payload)

export const deleteChannelById = (id) => api.delete(`/channel/${id}`)

// File
// Avatar
export const insertFile_avatar = (file) => api.post("/file_avatar", file)

export const getFileById_avatar = (id) => api.get(`/file_avatar/id/${id}`)

export const deleteFileById_avatar = (id) => api.delete(`/file_avatar/id/${id}`)

const apis = {
    registeringUser,
    getUserById,
    getUserByPseudo,
    updateUserById,
    deleteUserById,

    insertChannel,
    getChannelById,
    updateChannelById,
    deleteChannelById,

    insertFile_avatar,
    getFileById_avatar,
    deleteFileById_avatar
}

export default apis
