import React, { useRef } from 'react'
import { useContacts } from '../../contexts/ContactsProvider'
import { useConversations } from '../../contexts/ChannelsProvider'

function NewGroupe({ isShowing, hide }) {
	const idRef = useRef()
	const nameRef = useRef()
	const { createContact } = useContacts()
	const { createChannel } = useConversations()

	function handleSubmit(e) {
		e.preventDefault()
		// Création du canal de contacts
		createChannel([idRef.current.value])
		// Création du contact dans la liste des contacts
		createContact(idRef.current.value, nameRef.current.value)
		hide()
	}

	return (
		isShowing
			?
			<form className="createChannelPrivate" onSubmit={handleSubmit}>
				<div className="createChannelPrivate-header">
					<div className="title">Crée un contact</div>
				</div>
				<div className="createChannelPrivate-body">
					<input className="input" type="text" placeholder="Id" ref={idRef} required />
					<input className="input" type="text" placeholder="Name" ref={nameRef} required />
				</div>
				<div className="createChannelPrivate-footer">
					<button className="btn-text-small" type="submit">Ajouter</button>
				</div>
			</form>
			: null
	)
}
export default NewGroupe;
