import React from 'react'
import Connection from './Connection/Connection'
import useLocalStorage from '../hooks/useLocalStorage';
import Dashboard from './Dashboard'
import { ChannelsProvider } from '../contexts/ChannelsProvider';
import { SocketProvider } from '../contexts/SocketProvider';
import { UserProvider } from '../contexts/UserProvider';

function App() {
  // Je décompose mes fonctions 
  // Utilisation du stockage local pour l'id
  const [id, setId] = useLocalStorage('id')
  // Utilisation du stockage local pour le token
  const [token, setToken] = useLocalStorage('token')

  // Si l'id ou le token n'est pas présent il faut se connecter pour obtenir ces informations
  if (id === undefined || token === undefined) return (
    <Connection setId={setId} setToken={setToken} />
  )

  // Si toutes les informations sont présentes j'affiche le "Dashboard"
  return (
    <SocketProvider id={id}>
      <UserProvider id={id}>
        <ChannelsProvider id={id}>
          <Dashboard id={id} />
        </ChannelsProvider>
      </UserProvider>
    </SocketProvider>
  )
}

export default App;
