import React, { useState, useEffect } from 'react'
import Connection from './Connection/Connection'
import useLocalStorage from '../hooks/useLocalStorage';
import Dashboard from './Dashboard'
import api from '../api'
import { ChannelsProvider } from '../contexts/ChannelsProvider';
import { SocketProvider } from '../contexts/SocketProvider';
import { UserProvider } from '../contexts/UserProvider';

function App() {
  // Fonctions pour utiliser le stockage local pour l'id et le token
  const [id, setId] = useLocalStorage('id')
  const [token, setToken] = useLocalStorage('token')

  const [autoLoginValidity, setAutoLoginValidity] = useState(false)

  const connection = <Connection setId={setId} setToken={setToken} />

  // Le "useEffect" est utilisé pour géré l'asynchrone
  useEffect(() => {
    api.autologinUser({ id, token }).then(res => {
      if (!res.data.status.success) return connection
      if (!res.data.status.idValide) return connection
      if (!res.data.status.tokenValide) return connection

      if (!autoLoginValidity) {
        console.log(res.data)
        setToken(res.data.user.token)
        setAutoLoginValidity(true)
      }
    })
  }, [])

  // Si l'id ou le token n'est pas présent il faut se connecter pour obtenir ces informations
  if (id === undefined || token === undefined) {
    return connection
  }

  // Si toutes les informations sont présentes j'affiche le "Dashboard"
  if (autoLoginValidity) {
    const localStorage = { id, token }
    return (
      <SocketProvider localStorage={localStorage}>
        <UserProvider localStorage={localStorage}>
          <ChannelsProvider localStorage={localStorage}>
            <Dashboard localStorage={localStorage} />
          </ChannelsProvider>
        </UserProvider>
      </SocketProvider>
    )
  } else {
    return connection
  }

}

export default App;
