module.exports = {
  validityPseudo: function (target) {
    const x = target.textLength
    switch (true) {
      case (x === 0):
        return {warning: "", valid: false}
      case (x < 3):
        return {warning: "Le pseudo est trop court", valid: false}
      case (x > 32):
        return {warning: "Le pseudo est trop long", valid: false}
      default:
        return {warning: "", valid: true}
    }
  },
  validityPassword: function (target) {
    const x = target.textLength
    switch (true) {
      case (x === 0):
        return {warning: "", valid: false}
      case (x < 6):
        return {warning: "Le pseudo est trop court", valid: false}
      case (x > 32):
        return {warning: "Le pseudo est trop long", valid: false}
      default:
        return {warning: "", valid: true}
    }
  },
  validityPasswordConfirm: function (target, password) {
    const x = target.value
    switch (true) {
      case (x === 0):
        return {warning: "", valid: false}
      case (passwordConfirm === password):
        return {warning: "Le mot de passe ne corrrespond pas", valid: false}
      default:
        return {warning: "", valid: true}
    }
  }
};
