import React, { useState } from 'react'
import Conversations from './Conversations'
import Contacts from './Contacts'

import NewContact from "./NewContact";
import NewGroupe from "./NewGroupe";

import Setting from "./client/setting";
import Security from "./client/security";

export default function Sidebar({ id }) {

	// [ Toggle ]

	// Affiche le formulaire d'ajout de Contact
	const useModalNewContact = () => {
		const [isShowingNewContact, setIsShowing] = useState(false);

		// Inverseur
		function toggleNewContact() {
			setIsShowing(!isShowingNewContact);
		};
		return {
			isShowingNewContact,
			toggleNewContact
		};
	};
	const { isShowingNewContact, toggleNewContact } = useModalNewContact();

	// Affiche le formulaire d'ajout de groupe
	const useModalNewGroupe = () => {
		const [isShowingNewGroupe, setIsShowing] = useState(false);

		// Inverseur
		function toggleNewGroupe() {
			setIsShowing(!isShowingNewGroupe);
		}

		return {
			isShowingNewGroupe,
			toggleNewGroupe
		};
	};
	const { isShowingNewGroupe, toggleNewGroupe } = useModalNewGroupe();

	// Affiche la fenêtre de paramètre
	const useModalSetting = () => {
		const [isShowingSetting, setIsShowing] = useState(false);

		// Inverseur
		function toggleSetting() {
			setIsShowing(!isShowingSetting);
		}

		return {
			isShowingSetting,
			toggleSetting
		};
	};
	const { isShowingSetting, toggleSetting } = useModalSetting();

	// Affiche la fenêtre de sécurité
	const useModalSecurity = () => {
		const [isShowingSecurity, setIsSecurity] = useState(false);

		// Inverseur
		function toggleSecurity() {
			setIsSecurity(!isShowingSecurity);
		}

		return {
			isShowingSecurity,
			toggleSecurity
		};
	};
	const { isShowingSecurity, toggleSecurity } = useModalSecurity();

	// [ Return ]
	return (
		<div className="contact" id="contact">
			<div className="contact-header">
				<input className="contact-header-search" type="text" placeholder="Rechercher un(e) conversation/groupe" />
			</div>
			<div className="contact-body scroller">

				<div className="title">
					<span className="text">Conversations</span>
					<button className="button" type="button" aria-label="Créer un MP" onClick={toggleNewContact}>
						<svg x="0" y="0" aria-hidden="false"
							width="24" height="24" viewBox="0 0 18 18">
							<polygon fillRule="nonzero" fill="currentColor" points="15 10 10 10 10 15 8 15 8 10 3 10 3 8 8 8 8 3 10 3 10 8 15 8"></polygon>
						</svg>
					</button>
				</div>
				<div className="private-channels" id="channelsPrivates" aria-label="Salons privés">
					<NewContact isShowing={isShowingNewContact} hide={toggleNewContact} />
					<Conversations />
				</div>
				<div className="title" id="channelsGroupesTitle">
					<span className="text">Contacts</span>
					<button className="button" type="button" aria-label="Créer un Groupe" onClick={toggleNewGroupe}>
						<svg x="0" y="0" aria-hidden="false"
							width="24" height="24" viewBox="0 0 18 18">
							<polygon fillRule="nonzero" fill="currentColor" points="15 10 10 10 10 15 8 15 8 10 3 10 3 8 8 8 8 3 10 3 10 8 15 8"></polygon>
						</svg>
					</button>
				</div>
				<div className="groupe-channels" id="channelsGroupes" aria-label="Salons de groupes">
					<NewGroupe isShowing={isShowingNewGroupe} hide={toggleNewGroupe} />
					<Contacts />
				</div>

			</div>

			<div className="contact-footer">
				<div className="contact-footer-panels" aria-label="Zone utilisateur">
					<div className="contact-footer-panels-avatar">
						<svg width="40" height="32" viewBox="0 0 40 32" aria-hidden="true">
							<foreignObject x="0" y="0" width="32" height="32">
								<img className="avatar" src="https://cdn.discordapp.com/avatars/456863924210630657/1d92f56700533a272820d955d2dc4f29.png?size=128" aria-hidden="true" alt="" />
							</foreignObject>
							<rect className="online" fill="currentColor" width="10" height="10" x="22" y="22" rx="15" ry="15"></rect>
						</svg>
					</div>
					<div className="contact-footer-panels-info">
						<div className="username">
							<div className="overflow">{id}</div>
						</div>
						<div className="tag">
							<div className="overflow">#1012</div>
						</div>
					</div>
					<div className="contact-footer-panels-control">
						<button className="contact-footer-panels-control-btn" type="button" alt="Bouton clés" onClick={toggleSecurity}>
							<div className="contact-footer-panels-control-btn-icon">
								<svg viewBox="0 0 512 512" aria-hidden="false" width="17" height="17" x="0" y="0">
									<path fill="currentColor" d="M463.748,48.251c-64.336-64.336-169.013-64.335-233.349,0.001c-43.945,43.945-59.209,108.706-40.181,167.461
										L4.396,401.536c-2.813,2.813-4.395,6.621-4.395,10.606V497c0,8.291,6.709,15,15,15h84.858c3.984,0,7.793-1.582,10.605-4.395
										l21.211-21.226c3.237-3.237,4.819-7.778,4.292-12.334l-2.637-22.793l31.582-2.974c7.178-0.674,12.847-6.343,13.521-13.521
										l2.974-31.582l22.793,2.651c4.233,0.571,8.496-0.85,11.704-3.691c3.193-2.856,5.024-6.929,5.024-11.206V363h27.422
										c3.984,0,7.793-1.582,10.605-4.395l38.467-37.958c58.74,19.043,122.381,4.929,166.326-39.046
										C528.084,217.266,528.084,112.587,463.748,48.251z M421.313,154.321c-17.549,17.549-46.084,17.549-63.633,0
										s-17.549-46.084,0-63.633s46.084-17.549,63.633,0S438.861,136.772,421.313,154.321z" />
								</svg>
							</div>
						</button>
						<Security isShowing={isShowingSecurity} hide={toggleSecurity} />
						<button className="contact-footer-panels-control-btn" type="button" alt="Bouton paramètre" onClick={toggleSetting}>
							<div className="contact-footer-panels-control-btn-icon">
								<svg viewBox="0 0 24 24" aria-hidden="false" width="17" height="17" x="0" y="0">
									<path fill="currentColor"
										d="m22.683 9.394-1.88-.239c-.155-.477-.346-.937-.569-1.374l1.161-1.495c.47-.605.415-1.459-.122-1.979l-1.575-1.575c-.525-.542-1.379-.596-1.985-.127l-1.493
										1.161c-.437-.223-.897-.414-1.375-.569l-.239-1.877c-.09-.753-.729-1.32-1.486-1.32h-2.24c-.757 0-1.396.567-1.486 1.317l-.239
										1.88c-.478.155-.938.345-1.375.569l-1.494-1.161c-.604-.469-1.458-.415-1.979.122l-1.575 1.574c-.542.526-.597 1.38-.127 1.986l1.161
										1.494c-.224.437-.414.897-.569 1.374l-1.877.239c-.753.09-1.32.729-1.32 1.486v2.24c0 .757.567 1.396 1.317 1.486l1.88.239c.155.477.346.937.569
										1.374l-1.161 1.495c-.47.605-.415 1.459.122 1.979l1.575 1.575c.526.541 1.379.595 1.985.126l1.494-1.161c.437.224.897.415 1.374.569l.239
										1.876c.09.755.729 1.322 1.486 1.322h2.24c.757 0 1.396-.567 1.486-1.317l.239-1.88c.477-.155.937-.346 1.374-.569l1.495 1.161c.605.47
										1.459.415 1.979-.122l1.575-1.575c.542-.526.597-1.379.127-1.985l-1.161-1.494c.224-.437.415-.897.569-1.374l1.876-.239c.753-.09 1.32-.729
										1.32-1.486v-2.24c.001-.757-.566-1.396-1.316-1.486zm-10.683 7.606c-2.757 0-5-2.243-5-5s2.243-5 5-5 5 2.243 5 5-2.243 5-5 5z" />
								</svg>
							</div>
						</button>
						<Setting isShowing={isShowingSetting} hide={toggleSetting} id={id} />
					</div>
				</div>
			</div>

		</div>
	)
}
