import React, { Component } from 'react'
import { v4 as uuidV4 } from 'uuid'
const { validityPseudo } = require("../../functions/validity.js");

class Register extends Component {
  constructor(props) {
    super(props)
    console.log(props);
    this.state = {
      toggle: props.hide,
      pseudo: {
        value: '',
        warning: '',
        valid: false
      },
      password: {
        value: '',
        warning: '',
        valid: false
      },
      passwordConfirm: {
        value: '',
        warning: '',
        valid: false
      }
    }
  }

  handleChangeInputPseudo = async event => {

    ;
    console.log(warning)

    const value = event.target.value
    const warningt = "test"

    this.setState({
      pseudo: {
        value: value,
        warning: validityPseudo(event.target).warning,
        valid: validityPseudo(event.target).valid
      }
    })
  }

  handleChangeInputPassword = async event => {

    function validity(value) {
      const valueLength = value.textLength
      switch (true) {
        case (valueLength === 0):
          return null
        case (valueLength < 10):
          return "Le mot de passe est trop court"
        case (valueLength > 32):
          return "Le mot de passe est trop long"
        default:
          return false
      }
    }

    const value = event.target.value
    const warning = validity(event.target)

    this.setState({
      password: {
        value: value,
        warning: warning
      }
    })
  }

  handleChangeInputPasswordConfirm = async event => {
    function validity(value, thiss) {
      
      const valueValue = value.value
      switch (true) {
        case (value.textLength === 0):
          return null
        case (valueValue !== thiss.state.password.value):
          return "Le mot de passe ne correspond pas"
        default:
          return false
      }
    }

    const value = event.target.value
    const warning = validity(event.target, this)

    this.setState({
      passwordConfirm: {
        value: value,
        warning: warning
      }
    })
  }

  handleCreateUser = () => {
    this.props.onIdSubmit(uuidV4())
  }

  render() {
    const { pseudo, password, passwordConfirm } = this.state

    return (
      <div className="connection">
        <form className="connection-form">
          <div className="connectionToolHeader">
            <div className="connectionToolHeader-title">
              FInt.Tchat
            </div>
            <div className="connectionToolHeader-subtitle">
              Bienvenue dans l'espace d'inscription
            </div>
          </div>
          <div className="connection-body scroller">
            <div className="connection-body-input">
              <div className="title">
                Pseudo{pseudo.warning ? <span className="title-warning"> - {pseudo.warning}</span> : null}
              </div>
              <input 
                className={`input ${pseudo.warning ? 'input-warning' : ''}`}
                type="text"
                autoFocus
                required
                value={pseudo.value}
                onChange={this.handleChangeInputPseudo}
              />
              <div className="spacer"></div>
              <div className="title">
                Mot de passe{password.warning ? <span className="title-warning"> - {password.warning}</span> : null}
              </div>
              <input 
                className={`input ${password.warning ? 'input-warning' : ''}`}
                type="password"
                required
                value={password.value}
                onChange={this.handleChangeInputPassword}
              />
              <div className="spacer"></div>
              <div className="title">
                Confirmer le mot de passe{passwordConfirm.warning ? <span className="title-warning"> - {passwordConfirm.warning}</span> : null}
              </div>
              <input 
                className={`input ${passwordConfirm.warning ? 'input-warning' : ''}`}
                type="password"
                required
                value={passwordConfirm.value}
                onChange={this.handleChangeInputPasswordConfirm}
              />
            </div>
          </div>
          <div className="connectionToolFooter">
            <div className="connectionToolFooter-btn">
              <button className="btn-text-default" type="button" onClick={this.handleCreateUser}>
                <div className="content">S'inscrire</div>
              </button>
              <button className="btn-text-simple" type="button" onClick={this.props.hide}>
                <div className="content">Se connecter</div>
              </button>
            </div>
          </div>
        </form>
      </div>
    )
  }
}

export default Register
