import api from "../../api"

function reducertest(state) {
  return new Promise(resolve => {
      api.getUserById("60749c0d0c37e924d44dd26e").then(user => {
        resolve({ ...state, count: user.data.data.pseudo });
      })
  });
}

export const initialState = { 
  count: "f" };

function reducer(state) {
  return new Promise(resolve => {
    setTimeout(() => {

      resolve({ ...state, count: 1 });

    }, 1000);
  });
}

export default reducer;

