import api from "../../api"

function test() {
  return new Promise(resolve => {
    api.getUserById("f650a09d-c971-4140-ac1b-9a2355d11ce4").then(user => {
      return resolve({ user: user.data.data });
    })
  });
}
const address = api.getUserById("f650a09d-c971-4140-ac1b-9a2355d11ce4").then(user => { user.data.data })
const printAddress = async () => {
  const a = await address;
  console.log(a);
};

console.log(test())

export const initialState = { count: 0 };

function reducer(state, action) {
  return new Promise(resolve => {
    setTimeout(() => {
      switch (action.type) {
        case 'up':
          resolve({ ...state, count: state.count + 1 });
          break;
        case 'down':
          resolve({ ...state, count: state.count - 1 });
          break;
        case 'reset':
          resolve({ ...initialState });
          break;
        default:
          resolve(state);
          break;
      }
    }, 1000);
  });
}

export default reducer;
