import axios from 'axios'

const api = axios.create({
    baseURL: 'http://localhost:3000/api',
})

export const insertUser = (payload) => api.post(`/user`, payload)
export const getAllUsers = () => api.get(`/users`)
export const updateUserById = (id, payload) => api.put(`/user/${id}`, payload)
export const updateUserConversations = (id, payload) => api.put(`/user/conversations/${id}`, payload)
export const deleteUserById = (id) => api.delete(`/user/${id}`)
export const getUserById = (id) => api.get(`/user/id/${id}`)
export const getUserByPseudo = (pseudo) => api.get(`/user/pseudo/${pseudo}`)

const apis = {
    insertUser,
    getAllUsers,
    updateUserById,
    updateUserConversations,
    deleteUserById,
    getUserById,
    getUserByPseudo
}

export default apis
