import React, { Component, useContext } from 'react'

import api from "../api"

import SocketContext from './SocketProvider';

const UserContext = React.createContext()

export function useUser() {
  return useContext(UserContext)
}

export class UserProvider extends Component {
  static contextType = SocketContext
  constructor(props) {
    super(props);
    this.state = {
      user: null,
      error: null,
      loading: Boolean,
      requesting: Boolean
    };
  }

  componentDidMount() {
    if (this.state.requesting) {
      this.setState({
        loading: true,
        requesting: true
      });
      api.getUserById(this.props.id).then(
        (user) => {
          this.setState({
            user: user.data.data,
            loading: false,
            requesting: false
          });
        },
        (error) => {
          this.setState({
            loading: true,
            requesting: false,
            error
          });
        }
      )
    }
  }

