      activity: user.activity,
      conversations: user.conversations
    }

    await api.updateUser(user.id, payload).then(res => {
      
        this.setState({
            name: '',
            time: '',
        })
      
    })*/
    
  }

  deleteUser = () => {

  }

  render() {
    const value = {
      user: this.state.user
    }

    return (
      <UserContext.Provider value={value}>
        {this.props.children}
      </UserContext.Provider>
    )
  }
}

export default UserContext
