import React, { useContext, useState, useEffect, useCallback } from 'react'
import { useSocket } from './SocketProvider';

const ChannelsContext = React.createContext()

export function useChannels() {
  return useContext(ChannelsContext)
}

export function ChannelsProvider({ id, children }) {
  const [selectedChannel, setSelectedChannel] = useState(null)
  const socket = useSocket()

  function addMessageToChannel(sender, recipients, text) {
    // J'isole les données du client dans une variable pour pouvoir les modifier
    var userClientEdit = oldUser
    // Je boucle tous les "channels" du client
    for (let i = 0; i < userClientEdit.channels.length; i++) {
      // Je teste si l'id du "channel" sélectionné correspond à l'id du "channel" passé dans la boucle
      if (userClientEdit.channels[i].recipients.id === channel.recipients.id) {
        // Je supprime le "channel" dans la liste grace à sa position
        userClientEdit.channels.splice(i, 1);
        // Je mets à jour le client
        return api.updateUserById(userClientEdit.id, { data: { channels: userClientEdit.channels } }).then(res => {/*//EN COURS// Système gestion des erreurs*/})
      }
    }
  }
  /*
  const addMessageToChannel = useCallback(({ recipients, text, sender }) => {

    setChannels(prevChannels => {
      let madeChange = false
      const newMessage = { sender, text }
      const newChannels = prevChannels.map(channel => {
        if (arrayEquality(channel.recipients, recipients)) {
          madeChange = true
          return {
            ...channel,
            messages: [...channel.messages, newMessage]
          }
        }

        return channel
      })

      if (madeChange) {
        return newChannels
      } else {
        return [
          ...prevChannels,
          { recipients, messages: [newMessage] }
        ]
      }
    })
  }, [])*/

  useEffect(() => {
    if (socket == null) return

    socket.on('receive-message', addMessageToChannel)

    return () => socket.off('receive-message')
  }, [socket, addMessageToChannel])

  function sendMessage(recipients, text) {
    socket.emit('send-message', { recipients, text })

    addMessageToChannel({ recipients, text, sender: id })
  }

  const value = {
    selectedChannel: selectedChannel,
    setSelectedChannel,
    sendMessage
  }

  return (
    <ChannelsContext.Provider value={value}>
      {children}
    </ChannelsContext.Provider>
  )
}

function arrayEquality(a, b) {
  if (a.length !== b.length) return false

  a.sort()
  b.sort()

  return a.every((element, index) => {
    return element === b[index]
  })
}

export default ChannelsContext
