import React, { Component } from 'react'
import Sidebar from './Sidebar';
import OpenConversation from './OpenConversation';
import { useConversations } from '../contexts/ConversationsProvider';

import api from "../api"
// Main
class Dashboard extends Component {
  // id = this.props.id
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    const { selectedConversation } = useConversations();

    api.getUserById("f650a09d-c971-4140-ac1b-9a2355d11ce4").then(
      (user) => {
        this.setState({
          isLoaded: true,
          items: user.data.data
        });
      },
      (error) => {
        this.setState({
          isLoaded: true,
          error
        });
      }
    )
  }

  /*const req = api.getUserById("f650a09d-c971-4140-ac1b-9a2355d11ce4").then(user => { return user.data.data })
  const res = async () => {
    return await req
  };
  console.log(res())
  setState(res());*/

  render() {
    return (
      <>
        <Sidebar />
        <div className="container">
          {
            selectedConversation !== undefined ?
              selectedConversation && <OpenConversation />
              :
              <div className="welcome">
                <div className="welcome-title">
                  Bienvenue sur FInt.Tchat
                </div>
                <div className="welcome-content">
                  Pour ajouter un contact il faut cliquer sur le petit + a cot√© de contacts,
                  puis entrer son id et cliquez sur ajouter
                </div>
              </div>
          }
        </div>
      </>
    )
  }
}

export default Dashboard
