import React, { Component, useContext } from 'react'

import api from "../api"

const UserContext = React.createContext()

export function useUser() {
  return useContext(UserContext)
}

export class UserProvider extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: null,
      isLoaded: false,
      requesting: true,
      error: null
    };
  }

  componentDidMount() {
    if (this.state.requesting) {
      this.setState({
        isLoaded: true
      });
      api.getUserById(this.props.id).then(
        (user) => {
          this.setState({ 
            user: user.data.data,
            isLoaded: false,
            requesting: false
          });
        },
        (error) => {
          this.setState({
            isLoaded: false,
            requesting: false,
            error
          });
        }
      )
    }
  }

  createUser = () => {
    api.getUserById(this.props.id).then(
      (user) => {
        this.setState({ 
          user: user.data.data,
          isLoaded: false,
          requesting: false
        });
      },
      (error) => {
        this.setState({
          isLoaded: false,
          requesting: false,
          error
        });
      }
    )
  }

  updateUser = () => {

  }

  deleteUser = () => {

  }

  render() {
    const value = {
      user: this.state.user
    }

    return (
      <UserContext.Provider value={value}>
        {this.props.children}
      </UserContext.Provider>
    )
  }
}

export default UserContext
