module.exports = {
  validityPseudo: function (pseudo) {
    const x = pseudo.textLength
    console.log(x)
    switch (true) {
      case (x === 0):
        return {warning: "", valid: false}
      case (x < 3):
        return {warning: "Le pseudo est trop court", valid: false}
      case (x > 32):
        return {warning: "Le pseudo est trop long", valid: false}
      default:
        return {warning: "", valid: true}
    }
  },
  validityPassword: function (password) {
    const x = password.textLength
    switch (true) {
      case (x === 0):
        return {warning: "", valid: false}
      case (x < 6):
        return {warning: "Le mot de passe est trop court", valid: false}
      case (x > 32):
        return {warning: "Le mot de passe est trop long", valid: false}
      default:
        return {warning: "", valid: true}
    }
  },
  validityPasswordConfirm: function (passwordConfirm, password) {
    const passwordConfirmTextLength = passwordConfirm.textLength
    const passwordConfirmValue = passwordConfirm.value
    switch (true) {
      case (passwordConfirmTextLength === 0):
        return {warning: "", valid: false}
      case (passwordConfirmValue !== password):
        return {warning: "Incorrect", valid: false}
      default:
        return {warning: "", valid: true}
    }
  },
  validityPasswordConfirmForPassword: function (passwordConfirm, passwordValue) {
    const passwordConfirmTextLength = passwordConfirm.value.length
    const passwordConfirmValue = passwordConfirm.value
    switch (true) {
      case (passwordConfirmTextLength === 0):
        return {warning: "", valid: false}
      case (passwordConfirmValue !== passwordValue):
        return {warning: "Le mot de passe ne corrrespond pas", valid: false}
      default:
        return {warning: "", valid: true}
    }
  }
};
