import React, { useState } from 'react'
import api from "../../api"
import { useUser } from '../../contexts/UserProvider';
import { useChannels } from '../../contexts/ChannelsProvider';

function NewContact({ isShowing, hide, localStorage }) {
	const { client, updateUser } = useUser()
	const { channels, createChannel, setChannels, setSelectedChannel } = useChannels()

	console.log(client)
	// Création d'un "useState" pour stocker l'id
	const [id, setId] = useState({
		value: "",
		warning: ""
	})

	async function handleSubmit(e) {
		e.preventDefault()

		const idRecipient = id.value
		// Je teste si la valeur est bien celle d'un id
		if (id.value.length === 0) return setId({
			value: "",
			warning: "Il faut entrer un id"
		})

		// Je teste si la valeur est bien celle d'un id
		if (id.value.length !== 18 && !/^[0-9]*$/.test(id.value)) return setId({
			value: "",
			warning: "Ceci n'est pas un id valide"
		})

		// Je test si l'id n'est pas celui du client
		if (id.value === client.id) return setId({
			value: "",
			warning: "Tu ne peux pas t'ajouter"
		})

		// Je test si le contact n'est pas déjà ajouter
		var valid = true;
		client.channels.filter(channel => channel.type === "private").forEach(channel => {
			if (channel.id === id.value) valid = false;
		});
		if (valid === false) return setId({
			value: "",
			warning: "Ce contact existe déjà"
		});

		const newChannel = {
			id: (Math.floor(Math.random() * 10000000000000000) + 99999999999999999).toString(),
			type: "private",
			recipients: [client.id, id.value],
			messages: []
		}

		// Créaction du channel
		const res = await createChannel({
			client: {
				id: localStorage.id,
				token: localStorage.token
			},
			recipient: {
				id: id.value
			},
			channel: newChannel
		})

		if (res.status.success === false) return setId({
			value: "",
			warning: res.status.msg
		});

		setChannels(channels => [...channels, newChannel ]);

		var newChanForSelect = newChannel
		newChanForSelect.push({ id: idChannel, type: "private" })
/*
		setSelectedChannel({
			id: idChannel,
			type: "private",
			recipients: [{
				id: recipient.id,
				pseudo: recipient.pseudo
			}],
			messages: [],
			selected: true,
			key: channels.length === 0 ? 0 : channels.length - 1
		})

		var clientChannels = client.channels
		clientChannels.push({ id: idChannel, type: "private" })

		var recipientChannels = recipient.channels
		recipientChannels.push({ id: idChannel, type: "private" })

		// Mise à jour du client
		updateUser(client.id, { channels: clientChannels })

		// Mise à jour du destinataire
		updateUser(recipient.id, { channels: recipientChannels })
		*/

		setId({
			value: "",
			warning: ""
		})
		hide()

	}

	return (
		isShowing
			?
			<>
				<form className="createChannelPrivate" onSubmit={handleSubmit}>
					<div className="createChannelPrivate-header">
						<div className="title">Ajouter un contact</div>
					</div>
					<div className="createChannelPrivate-body">
						{id.warning ? <div className="title-warning">{id.warning} </div> : null}
						<input className="input" type="text" placeholder="Id"
							value={id.value}
							onChange={e => setId({
								value: e.target.value,
								warning: ""
							})}
						/>
					</div>
					<div className="createChannelPrivate-footer">
						<button className="btn-text-small" type="submit">Ajouter</button>
					</div>
				</form>
			</>
			: null
	)
}
export default NewContact;
